export const formatGigDate = () => {
    // Get the month spelled out (not int value).
    var options = { month: 'long' };
    var month = new Intl.DateTimeFormat('en-US', options).format(event);

    return `${event.getDate()} ${month} ${event.getFullYear()}`;
}
