export const formatGigDate = (rawDate) => {
    // Get the month spelled out (not int value).
    var options = { month: 'long' };
    var month = new Intl.DateTimeFormat('en-US', options).format(rawDate);

    return `${rawDate.getDate()} ${month} ${rawDate.getFullYear()}`;
}

export const formatFinalDate = () => {
    let date = formatGigDate(this.state.gig_date);

    if (this.state.festival_end_date && this.state.gig_date.getTime() != this.state.festival_end_date.getTime()) {
        let end_date = formatGigDate(this.state.festival_end_date);
        date += ' - ' + end_date;
    }

    return date;
}
